import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CourseService from "../services/course.services";

const CourseComponent = (props) => {
  const navigate = useNavigate();
  // eslint-disable-next-line
  let { currentUser, setCurrentUser } = props;
  let [courseData, setCourseData] = useState(null);

  const handleLogin = () => {
    navigate("/login");
  };

  useEffect(() => {
    console.log("using effect");
    console.log(currentUser);

    let _id = currentUser.user._id;

    if (currentUser.user.role === "instructor") {
      CourseService.showCourse(_id)
        .then((data) => {
          setCourseData(data.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
    // } else if (currentUser.user.role === "student") {
    //   CourseService.getEnrolled(_id)
    //     .then((data) => {
    //       console.log(data.data);
    //       setCourseData(data.data);
    //     })
    //     .catch((error) => {
    //       console.log(error);
    //     });
    // }
  }, [currentUser]);

  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && (
        <div>
          <p>你必須先登入才能看到此頁面</p>
          <button onClick={handleLogin} className="btn btn-dark btn-lrg">
            登入
          </button>
        </div>
      )}

      {/* 老師頁面 */}
      {currentUser && courseData && courseData.length !== 0 ? (
        <div>
          <h1>{currentUser.user.username}老師的課程清單</h1>
          {courseData.map((course) => (
            <div className="card" style={{ marginBottom: "1rem" }}>
              <div className="card-body">
                <h5 className="card-title">{course.title}</h5>
                <p className="card-text">{course.description}</p>
                {course.students && <p>上課人數： {course.students.length}</p>}
                <button className="btn btn-dark">NTD: {course.price}</button>
                <br />
              </div>
            </div>
          ))}
        </div>
      ) : null}

      {/* 學生頁面 */}
      {currentUser && currentUser.user.role === "student" && (
        <div>
          <h1>{currentUser.user.username}的課程清單</h1>
        </div>
      )}
    </div>
  );
};

export default CourseComponent;
