import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import courseServices from "../services/course.services";

const EnrollComponent = (props) => {
  let { currentUser, setCurrentUser } = props;
  const navigate = useNavigate();
  let [searchInput, setSearchInput] = useState("");
  let [searchResult, setSearchResult] = useState("");

  const handleLogin = () => {
    navigate("/login");
  };
  const handleChangeInput = (e) => {
    setSearchInput(e.target.value);
  };
  const handleSearch = () => {
    courseServices
      .getCourseByName(searchInput)
      .then((data) => {
        console.log(data);
        setSearchResult(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const handleEnroll = (e) => {
    courseServices
      .enroll(e.target.id, currentUser.user._id)
      .then(() => {
        window.alert("課程已註冊");
        navigate("/course");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && (
        <div>
          <p>你必須先登入才能看到此頁面</p>
          <button onClick={handleLogin} class="btn btn-dark btn-lg">
            登入
          </button>
        </div>
      )}

      {currentUser && currentUser.user.role === "student" && (
        <div>
          <h1>尋找課程</h1>
        </div>
      )}

      {currentUser && currentUser.user.role === "student" && (
        <div className="search input-group mb-3">
          <input
            onChange={handleChangeInput}
            type="text"
            class="form-control"
            placeholder="輸入您想找的課程名稱"
          />
          <button onClick={handleSearch} className="btn btn-dark">
            尋找課程
          </button>
        </div>
      )}

      {currentUser && searchResult && searchResult.length !== 0 ? (
        <div>
          <h3>以下是您尋找的課程清單</h3>
          {searchResult.map((course) => (
            <div className="card" style={{ marginBottom: "1rem" }}>
              <div className="card-body">
                <h5 className="card-title">{course.title}</h5>
                <p className="card-text">{course.description}</p>
                <p className="card-text">NTD: {course.price}</p>
                {course.students && <p>上課人數： {course.students.length}</p>}
                <a
                  href="#"
                  onClick={handleEnroll}
                  className="btn btn-dark"
                  id={course._id}
                >
                  選擇課程
                </a>
                <br />
              </div>
            </div>
          ))}
        </div>
      ) : null}
    </div>
  );
};

export default EnrollComponent;
