import axios from "axios";
const API_URL = "http://localhost:8080/api/course";

class CourseService {
  //新增課程串接
  postCourse(title, description, price) {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user"));
    } else {
      token = "";
    }
    return axios.post(
      API_URL,
      {
        title,
        description,
        price,
      },
      {
        headers: {
          Authorization: token,
        },
      }
    );
  }

  //顯示特定老師的課程串接
  showCourse(title, description, price) {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user"));
    } else {
      token = "";
    }

    return axios.get(API_URL + "/instructor/" + user.user._id, {
      headers: {
        Authorization: token,
      },
    });
  }
}

// eslint-disable-next-line
export default new CourseService();
