import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CourseService from "../services/course.services";

const PostCourseComponent = (props) => {
  let [courseData, setCourseData] = useState(null);
  let { currentUser, setCurrentUser } = props;
  let [title, setTitle] = useState("");
  let [description, setDescription] = useState("");
  let [price, setPrice] = useState(0);
  let [message, setMessage] = useState("");

  const navigate = useNavigate();
  const handleLogin = () => {
    navigate("/login");
  };
  const handleChangeTitle = (e) => {
    setTitle(e.target.value);
  };
  const handleChangeDescription = (e) => {
    setDescription(e.target.value);
  };
  const handleChangePrice = (e) => {
    setPrice(e.target.value);
  };
  const postCourse = () => {
    CourseService.postCourse(title, description, price)
      .then(() => {
        window.alert("課程已被成功新增");
        navigate("/course");
      })
      .catch((error) => {
        console.log(error);
        setMessage(error.response.data);
      });
  };

  useEffect(() => {
    console.log("using effect");
    console.log(currentUser);
    let _id = currentUser.user._id;
    console.log(currentUser.user.role);

    if (currentUser.user.role === "instructor") {
      CourseService.showCourse(_id)
        .then((data) => {
          console.log(data.data);
          setCourseData(data.data);
        })
        .catch((error) => {
          console.log(error);
        });
    } else if (currentUser.user.role === "student") {
      CourseService.getEnrolled(_id)
        .then((data) => {
          console.log(data.data);
          setCourseData(data.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, []);

  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && (
        <div>
          <p>你必須先登入才能看到此頁面</p>
          <button onClick={handleLogin} class="btn btn-dark btn-lg">
            登入
          </button>
        </div>
      )}

      {currentUser && currentUser.user.role !== "instructor" && (
        <div>
          <h1>只有老師才能新增課程</h1>
        </div>
      )}

      {currentUser && currentUser.user.role === "instructor" && (
        <div>
          <h1>{currentUser.user.username}老師的新增課程頁面</h1>
        </div>
      )}

      {currentUser && currentUser.user.role === "instructor" && (
        <div className="form-group">
          <label for="forTitle">課程標題</label>
          <input
            name="title"
            type="text"
            className="form-control"
            id="forTitle"
            onChange={handleChangeTitle}
          />
          <br />
          <label for="forDescription">課程描述</label>
          <textarea
            name="description"
            className="form-control"
            id="forDescription"
            aria-describedby="emailHelp"
            onChange={handleChangeDescription}
          />
          <br />
          <label for="forPrice">課程價錢</label>
          <input
            name="price"
            type="number"
            className="form-control"
            id="forPrice"
            onChange={handleChangePrice}
          />
          <br />
          <button className="btn btn-dark" onClick={postCourse}>
            新增課程
          </button>
          <br />
          <br />
          {message && <div className="alert alert-warning">{message}</div>}
        </div>
      )}

      {currentUser && courseData && courseData.length !== 0 && (
        <div>
          <p>Data we got back from API.</p>
          {courseData.map((course) => (
            <div className="card" style={{ width: "18rem" }}>
              <div className="card-body">
                <h5 className="card-title">{course.title}</h5>
                <p className="card-text">{course.description}</p>
                <p>Price: {course.price}</p>
                <p>Student: {course.students.length}</p>
                <a href="#" className="card-text btn btn-primary">
                  See Course
                </a>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PostCourseComponent;
