import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CourseService from "../services/course.services";

const CourseComponent = (props) => {
  const navigate = useNavigate();
  // eslint-disable-next-line
  let { currentUser, setCurrentUser } = props;
  let [courseData, setCourseData] = useState(null);

  const handleLogin = () => {
    navigate("/login");
  };

  useEffect(() => {
    console.log("using effect");

    let _id = currentUser.user._id;

    if (currentUser.user.role === "instructor") {
      CourseService.showCourse(_id)
        .then((data) => {
          setCourseData(data.data);
        })
        .catch((error) => {
          console.log(error);
        });
    } else if (currentUser.user.role === "student") {
      CourseService.showEnrolledCourse()
        .then((data) => {
          setCourseData(data.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [currentUser]);

  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && (
        <div>
          <p>你必須先登入才能看到此頁面</p>
          <button onClick={handleLogin} className="btn btn-dark btn-lrg">
            登入
          </button>
        </div>
      )}
      {/* 老師頁面 */}
      {currentUser && currentUser.user.role === "instructor" && (
        <h1>{currentUser.user.username}老師的課程清單</h1>
      )}
      {currentUser.user.role === "instructor" &&
      courseData &&
      courseData.length !== 0 ? (
        <div>
          {courseData.map((course) => (
            <div
              key={course._id}
              className="card"
              style={{ marginBottom: "1rem" }}
            >
              <div className="card-body">
                <h5 className="card-title">{course.title}</h5>
                <p className="card-text">{course.description}</p>
                <p>上課人數： {course.student.length} 人</p>
                <button className="btn btn-dark fs-5">$ {course.price}</button>
                <br />
              </div>
            </div>
          ))}
        </div>
      ) : null}
      {/* 學生頁面 */}
      {currentUser && currentUser.user.role === "student" && (
        <h1>{currentUser.user.username}的課程清單</h1>
      )}
      {currentUser.user.role === "student" &&
      courseData &&
      courseData.length !== 0 ? (
        <div>
          {courseData.map((course) => (
            <div
              key={course._id}
              className="card"
              style={{ marginBottom: "1rem" }}
            >
              <div className="card-body">
                <h5 className="card-title">{course.title}</h5>
                <p className="card-text">{course.description}</p>
                <p className="card-text">{course.instructor.username}</p>
                <p>上課人數： {course.student.length} 人</p>
                <button className="btn btn-dark fs-5">$ {course.price}</button>
                <br />
              </div>
            </div>
          ))}
        </div>
      ) : null}
    </div>
  );
};

export default CourseComponent;
